hudson.tasks.Shell.command:
newbranch=${Branch}-new-packaging
git checkout ${Branch}
git branch -D ${newbranch} > /dev/null 2>&1 || :
git branch ${newbranch}
git checkout ${newbranch}
deploy/build.sh --os=fc22 --release --updatepkg
deploy/build.sh --os=ubuntu14 --release --updatepkg
deploy/build.sh --os=raspberrypi --release --updatepkg
if [ ! "$(git diff)" = "" ]
then
  git commit -a -m "Update package content files"
  git push -f origin ${newbranch}
fi
